var Table = require('./')
var data = ''
process.stdin.on('data', function (d) {
  data += d
})
.on('end', function () {
  data = data.trim()

  if(data[0] == '{' || data[0] == '[')
    data = JSON.parse(data)
  

  if(~process.argv.indexOf('--JSON'))
    console.log(JSON.stringify(Table.createTable(data), null, 2))
  else
    console.log(Table.createTable(data).toCSV())

})
